#!/usr/bin/env python3
"""Batch add 330+ questions from previous HEO/ICAR/AFO exam papers to all JSON files."""
import json
import os

BASE = os.path.dirname(os.path.abspath(__file__))

def append_questions(filepath, new_questions):
    with open(filepath, 'r', encoding='utf-8') as f:
        existing = json.load(f)
    existing.extend(new_questions)
    with open(filepath, 'w', encoding='utf-8') as f:
        json.dump(existing, f, indent=4, ensure_ascii=False)
    print(f"  {os.path.basename(filepath)}: added {len(new_questions)}, total now {len(existing)}")

